package com.poonam.simplymockitoimport android.os.Bundleimport androidx.appcompat.app.AppCompatActivityimport androidx.lifecycle.ViewModelProviderimport androidx.lifecycle.ViewModelProvidersimport com.poonam.simplymockito.feature.profile.ProfileActivityimport dagger.android.AndroidInjectionimport kotlinx.android.synthetic.main.activity_main.*import javax.inject.Injectclass MainActivity : AppCompatActivity() {    @Inject    internal lateinit var viewModelFactory: ViewModelProvider.Factory    val mainViewModel by lazy {        ViewModelProviders.of(this,viewModelFactory).get(MainViewModel::class.java)    }    override fun onCreate(savedInstanceState: Bundle?) {        AndroidInjection.inject(this)        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_main)        btnShow.setOnClickListener {          startActivity(ProfileActivity.newIntent(this,tvName.text.toString()))        }    }}