package com.poonam.simplymockitoimport android.content.Contextimport com.poonam.simplymockito.di.component.DaggerAppComponentimport dagger.android.AndroidInjectorimport dagger.android.DaggerApplicationclass SimplyMockitoApplication : DaggerApplication(){    init {        instance = this    }    companion object {        private var instance: SimplyMockitoApplication? = null        fun applicationContext(): Context {            return instance!!.applicationContext        }    }    override fun applicationInjector(): AndroidInjector<out DaggerApplication> {        return DaggerAppComponent            .builder()            .application(this@SimplyMockitoApplication)            .build()    }}