package com.poonam.simplymockito.di.componentimport android.app.Applicationimport com.poonam.simplymockito.SimplyMockitoApplicationimport com.poonam.simplymockito.di.module.*import dagger.BindsInstanceimport dagger.Componentimport dagger.android.AndroidInjectorimport dagger.android.support.AndroidSupportInjectionModuleimport javax.inject.Singleton@Singleton@Component(    modules =    [        ActivityModule::class,        ViewModelModule::class,        ContextModule::class,        AppModule::class,        ApiModule::class,        AndroidSupportInjectionModule::class    ])interface AppComponent : AndroidInjector<SimplyMockitoApplication> {    @Component.Builder    interface Builder {        @BindsInstance        fun application(application: SimplyMockitoApplication): Builder        fun build(): AppComponent    }}