package com.poonam.simplymockito.di.moduleimport androidx.lifecycle.ViewModelimport androidx.lifecycle.ViewModelProviderimport com.poonam.simplymockito.di.factory.ViewModelFactoryimport com.poonam.simplymockito.di.key.ViewModelKeyimport com.poonam.simplymockito.feature.profile.ProfileViewModelimport dagger.Bindsimport dagger.Moduleimport dagger.multibindings.IntoMap@Moduleabstract class ViewModelModule{    @Binds    abstract fun bindViewModelFactory(factory : ViewModelFactory) : ViewModelProvider.Factory    @Binds    @IntoMap    @ViewModelKey(ProfileViewModel::class)    protected abstract fun profileViewModel(profileViewModel: ProfileViewModel) : ViewModel}