package com.poonam.simplymockito.feature.repoimport com.poonam.simplymockito.api.ApiInterfaceimport com.poonam.simplymockito.feature.data.GitUserProfileResponseimport io.reactivex.Observableimport io.reactivex.android.schedulers.AndroidSchedulersimport io.reactivex.schedulers.Schedulersclass GitRepo (val apiInterface: ApiInterface){    fun getGitProfile(userLoginName : String) : Observable<GitUserProfileResponse>? {        return apiInterface.getGitProfile(userLoginName)          .subscribeOn(Schedulers.io())          .observeOn(AndroidSchedulers.mainThread()) }}