package com.poonam.simplymockito.utilsclass LiveDataResult<T>(val status: Status, val data: T? = null, val err: Throwable? = null) {    companion object {        fun <T> success(data: T?) = LiveDataResult(Status.SUCCESS, data)        fun <T> error(err: Throwable?) = LiveDataResult<T>(Status.ERROR, null, err)        fun <T> loading() = LiveDataResult<T>(Status.LOADING)    }    enum class Status {        SUCCESS, ERROR, LOADING    }}